public MainWindow()
  {
      this.CascadingCheckBox_1.CascadingChildItemsSource = new List<CascadingCheckBox>() 
      {
          //令CascadingCheckBox_1_1,CascadingCheckBox_1_2與CascadingCheckBox_1作連動
          this.CascadingCheckBox_1_1,this.CascadingCheckBox_1_2
      };
      //更新父CascadingCheckBox的核取狀態(以其子項目為依據)
      this.CascadingCheckBox_1.notifySelfChildChanged();
  }

