using System.Windows.Media.Imaging;
using System.Windows.Navigation;
using System.Windows.Shapes;
using TEC.Core.Wpf.Controls.Option;
using TEC.Core.WPFExample.Settings;
namespace TEC.Core.WPFExample.Option
{
    /// <summary>
    /// ExampleUserControl.xaml 的互動邏輯
    /// </summary>
    public partial class ExampleUserControl : OptionUserControlBase<SomeSettingsEnum, object, Guid>
    {
        public ExampleUserControl()
        {
            InitializeComponent();
            base.SettingCollectionChanged += this.ExampleUserControl_SettingCollectionChanged;
        }
        private void ExampleUserControl_SettingCollectionChanged(object sender, EventArgs e)
        {
            //這裡可以將讀入設定檔集合中的值設定至UI中
            //例:  this.Txt_SomeValue.Text = this.SettingCollection[Settings.SomeSettingsEnum.SomeSettingKey].ToString();
        }
        public override void commitChanges()
        {
            //這裡將目前頁面中的值寫入到設定集合中
            this.SettingCollection[Settings.SomeSettingsEnum.SomeSettingKey] = "SomeValue";
        }
        public override List<ValidationResult> ValidateResult
        {
            get
            {
                //這裡實作驗證，也可以與UI動作
                return new List<ValidationResult>();
            }
        }
    }
}

 //MainControl.xaml.cs
 namespace TEC.Core.WPFExample
{
    /// <summary>
    /// MainWindow.xaml 的互動邏輯
    /// </summary>
    public partial class MainWindow : Window
    {
        public MainWindow()
        {
            //for first run
            InitializeComponent();
            TEC.Core.Wpf.Controls.Option.OptionWindow optionWindow = new Controls.Option.OptionWindow();
            optionWindow.OptionUserControlChanging += (sender, e) =>
            {
                //當每次選到其他UserControl前發生
                //可以由參數e取得變更前與變更後的使用者控制項
            };
            optionWindow.OptionUserControlChanged += (sender, e) =>
            {
                //當每次選到其他UserControl後發生
                //可以由參數e取得變更前與變更後的使用者控制項
            };
            optionWindow.OptionComfirmed += (sender_OptionComfirmed, e_OptionComfirmed) =>
            {
                //從e_OptionComfirmed之中可以取得所有變更後的設定集合
                var changedSettingCollection =  e_OptionComfirmed.RegistedSettingCollections.OfType<SomeSettingCollection>().First();
                //接著您可以將設定檔儲存至任何的資料來源中
            };
            optionWindow.registerSettingUserControl("設定\\一般", typeof(TEC.Core.WPFExample.Option.ExampleUserControl), 
                     new Settings.Collection.SomeSettingCollection().DefaultValues);
            //您也可以透過SettingCollectionBase.Clone()方法建立複本
            optionWindow.registerSettingUserControl("設定\\一般2", typeof(TEC.Core.WPFExample.Option.ExampleUserControl), 
                     (SomeSettingCollection)getSomeSettingCollection().Clone());
            //optionWindow.unRegisterSettingUserControl(typeof(TEC.Core.WPFExample.Option.ExampleUserControl));//用於取消註冊
            optionWindow.Show();
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Windows;
using System.Windows.Controls;
using System.Windows.Data;
using System.Windows.Documents;
using System.Windows.Input;
using System.Windows.Media;
using System.Windows.Media.Imaging;
using System.Windows.Shapes;

namespace TEC.Core.WPFExample.Controls
{
    public partial class RingSliceMainWindow : Window
    {
        public RingSliceMainWindow()
        {
            InitializeComponent();
        }
        private void Window_Loaded(object sender, RoutedEventArgs e)
        {
            Task.Run(async () =>
            {
                System.Threading.Thread.Sleep(2000);
                await this.Dispatcher.BeginInvoke(new Action(() =>
                {
                    this.RingSliceProgress_Loading.RingSliceState = Core.Controls.RingSliceState.Paused;
                }));
                System.Threading.Thread.Sleep(2000);
                await this.Dispatcher.BeginInvoke(new Action(() =>
                {
                    this.RingSliceProgress_Loading.RingSliceState = Core.Controls.RingSliceState.InProgress;
                }));
                System.Threading.Thread.Sleep(2000);
                await this.Dispatcher.BeginInvoke(new Action(() =>
                {
                    this.RingSliceProgress_Loading.RingSliceState = Core.Controls.RingSliceState.Error;
                }));
                System.Threading.Thread.Sleep(2000);
                await this.Dispatcher.BeginInvoke(new Action(() =>
                {
                    this.RingSliceProgress_Loading.RingSliceState = Core.Controls.RingSliceState.InProgress;
                }));
                System.Threading.Thread.Sleep(2000);
                await this.Dispatcher.BeginInvoke(new Action(() =>
                {
                    this.RingSliceProgress_Loading.RingSliceState = Core.Controls.RingSliceState.Success;
                }));
            });
        }
    }
}

