public partial class App : Application
{
    [STAThread]
    public static void Main()
    {
        //本程式碼應放到適當的位置(顯示要區域化的Actipro產品之前)
        //先讀取Actipro產品到CurrentDomain中
        Enum.GetValues(typeof(TEC.Core.Wpf.Globalization.ActiproStringResource.ActiproProducts))
          .Cast<TEC.Core.Wpf.Globalization.ActiproStringResource.ActiproProducts>()
          .AsParallel()
          .ForAll(product =>
          {
              string productAssemblyName = TEC.Core.Wpf.Globalization.ActiproStringResource.getAssemblyName(product);
              //判斷目前的組件中是否已經讀取了要區域化的Actipro產品
              if (!AppDomain.CurrentDomain.GetAssemblies().Any(t => t.FullName.Equals(productAssemblyName)))
              {
                  try
                  {
                      AppDomain.CurrentDomain.Load(productAssemblyName);
                  }
                  catch (System.IO.FileNotFoundException)
                  {
                      //當應用程式沒有指定的產品組件時
                      return;
                  }
              }
          });
        //讀取資源檔
        TEC.Core.Wpf.Globalization.ActiproStringResource.loadAllProductResource();
        ThemeManager.BeginUpdate();
        try
        {
            // 若您要使用Office以及Metro的樣板時，都必須要在使用他之前用以下程式碼進行註冊
            //ThemesOfficeThemeCatalogRegistrar.Register();
            ThemesMetroThemeCatalogRegistrar.Register();
            // 預設都使用Actipro的產品樣式
            ThemeManager.AreNativeThemesEnabled = true;
            // 套用Metro Dark樣板
            ThemeManager.CurrentTheme = ThemeName.MetroDark.ToString();
        }
        finally
        {
            ThemeManager.EndUpdate();
        }
    }
}

