 public partial class App : Application, ISingleInstanceApp
{
    private const string unique = "My_Unique_Application_String";//必須要確保唯一
    //定義Main方法前，請先從方總管中江App.xaml檔案的「建置動作」由「Application Definition」改為「Page」
    //接著請在專案的設定中，將起始物件設定為「WpfApplication.App」
    [STAThread]
    public static void Main()
    {
        if (SingleInstance<App>.InitializeAsFirstInstance(unique))
        {
            var application = new App();
            application.InitializeComponent();
            application.Run();
            // 令單一執行個體清除資源
            SingleInstance<App>.Cleanup();
        }
    }
    private void App_Startup(object sender, StartupEventArgs e)
    {
        MainWindow mainWindow = new MainWindow(e);
        mainWindow.Show();
    }
    #region ISingleInstanceApp 成員
    public bool SignalExternalCommandLineArgs(IList<string> args)
    {
        // 處理命令列參數
        // ...
        return true;
    }
    #endregion
}
